import React from 'react'
import { Col, Row } from 'react-bootstrap'
import WeatherCard from './WeatherCard'

const WeatherList = ({ weathers, city }) => {
    //console.log(city.name);
    //console.log(weathers);
    return (
        <Row>
            {weathers.map(({ dt, main, wind, weather, name }) => (
                <Col key={dt}>
                    <WeatherCard temp_max={main.temp_max} temp_min={main.temp_min} temp={main.temp} humidity={main.humidity} pressure={main.pressure} wind_speed={wind.speed} wind_direction={wind.gust} dt={dt * 1000} main={weather[0].main} icon={weather[0].icon} name={city.name} />
                </Col>
            ))}
        </Row>
    )
}

export default WeatherList;

