import { getDepartments, getProducts } from "../../services/catalog";

export const setFilter = department => {
  return {
    type: "SET_FILTER",
    filter: department
  };
};

export const clearFilter = () => {
  return {
    type: "CLEAR_FILTER"
  };
};

export const initCatalog = () => dispatch => {
  Promise.all([getDepartments(), getProducts])

}
